import React from "react";
import data from "./data";

export default function Card(props) {
  console.log(props.img);
  let badgetext;

  if (props.data.openspot === 0) {
    badgetext = "SOLD OUT";
  } else if (props.data.location === "online") {
    badgetext = "online";
  }

  return (
    <div className="card">
      {badgetext && <div className="card--badge">{badgetext}</div>}

      <img src={`./img/${props.data.coverImg}`} className="hero-img" />

      <div className="info--para">
        <img src="./img/Star.png" width="16.5px" />

        <span>&nbsp;{props.data.stats.rating} </span>
        <span className="gray">({props.data.stats.revcount}).</span>
        {/* Assuming location is not part of props.data.stats */}
        {/* If it is, replace with props.data.stats.location */}
        <span className="gray"> ({props.data.location})</span>
      </div>
      <p>{props.data.title}</p>
      <p>From Lessons with {props.data.title}</p>
      <p>
        <span className="bold">From ${props.data.price}</span> / person
      </p>
    </div>
  );
}
