import './App.css';
import React from 'react';
import datas  from './components/memsdata';

import Travel from './components/Meme';
import Meme from './components/Meme'
import Eve from './components/Evente';

import Memeimp from './components/Memecop';
import Counter from './components/Counter';
import Ternary from './components/ternary';
import Carte from './components/cartevis';
import Body from './components/Body';
import Header from './components/header';
import Boxes from './components/Boxes';
import Boxe from './components/Boxe.js';

function App(props) {
  const [user, setUser] = React.useState("joe");
  const [squares, setSquares] = React.useState(Boxes); 
  function toggle(id) {
    console.log("wasa");
    setSquares(prevsquare => {
      const newsquare = [];
      for (let i = 0; i < prevsquare.length; i++) {
        const currentsquare = prevsquare[i];
        if (currentsquare.id === id) {
          const updatesquare = {
            ...currentsquare,
            on: !currentsquare.on
          };
          newsquare.push(updatesquare);
        } else {
          newsquare.push(currentsquare);
        }
      }
      return newsquare;
    });
  }



  const boxElements = squares.map(square => (
   <Boxe key={square.id}
    on={square.on}
    toggle={toggle} 
    id={square.id}
      />
  ));





//    < Ternary/> <Meme/>  <Carte/>
 //  <Memeimp/>

// Eve
  return (

    <div>
       {boxElements}





    </div>
  );
}

export default App;
