    function handleSUB(event) {
      const {name,value,type,checked}=event.target
      setformdata((prevdata) => ({
        ...prevdata,
  
        [name]: type==="checked"? checked : value,
      
      }));
    }


    return(
        <form className="formdata">
            <label htmlFor="email">votre email</label>
            <input onChange={hundlLogin} className="inputdata" placeholder="votre email" type="text" value={login.email}
            name="email" />
            <label >votre password</label>
            <input onChange={hundlLogin} type="password" className="inputdata" placeholder="password" name="pass" />
            <label>confirm password </label>
            <input  className="inputdata" type="password" placeholder="confirm votre password" />
           <button>submit </button>

            
        </form>
    )
}
