import React,{useState} from "react";
export default function Box(props){


    const [satus,setStatus]=React.useState(props.on)
    function changeStatus(){
        setStatus(
           
          prevstatus=>{
            return(
                prevstatus=!prevstatus

            
            )
          }

            
            
        )
    }

    const styles={
        backgroundColor:satus ?  ["GREEN"] :  "RED"
    }


    return(
        <div  className="box" style={styles}  onClick={changeStatus} ></div>

    )
    
}
