import React from "react";
import data from "./data";

export default function Card(props) {
  let badgetext;

  if (props.openspot === 0) {
    badgetext = "SOLD OUT";
  } else if (props.location === "online") {
    badgetext = "online";
  }

  return (
    <div className="card">
      {badgetext && <div className="card--badge">{badgetext}</div>}

      <img
        src={`./img/${props.coverImg}`}
        alt={`Cover image for ${props.item.title}`}
        className="hero-img"
      />

      <div className="info--para">
        <img src="./img/Star.png" alt="Star icon" width="16.5px" />

        <span>&nbsp;{props.item.stats}</span>
        <span className="gray">({props.revcount}).</span>
        <span className="gray"> ({props.location})</span>
      </div>
      <p>{props.item.title}</p>
      <p>From Lessons with {props.item.title}</p>
      <p>
        <span className="bold">From ${props.price}</span> / person
      </p>
    </div>
  );
}
