import React from "react";

export default function Card(props) {
  return (
    <div className="car">
      {/* Conditionally render the first image based on the presence of props.coverImg */}
     

      {/* Render the second image */}
      <img src={`./img/${props.coverImg}`} className="hero--img" />

      <div className="info--para">
        <img src="./img/Star.png" width="16.5px" />

        <span>&nbsp;{props.stats} </span>
        <span className="gray">({props.revcount}).</span>
        <span className="gray">{props.location}</span>
      </div>
      <p>{props.title}</p>
      <p>From Lessons with {props.title}</p>
      <p>
        <span className="bold">From ${props.price}</span> / person
      </p>
    </div>
  );
}
